import React, { useEffect, useState } from 'react';
import { Bar, Pie, Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  PointElement,
  LineElement,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  PointElement,
  LineElement
);

const Charts = ({ prediction }) => {
  const [delayHistory, setDelayHistory] = useState([0.1, 0.3, 0.2, 0.5, 0.7]); // Simulated data

  // Bar Chart Data
  const barData = {
    labels: ['Probability of Delay'],
    datasets: [
      {
        label: 'Probability (%)',
        data: [prediction ? prediction.probability_of_delay * 100 : 0],
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
      },
    ],
  };

  // Pie Chart Data
  const pieData = {
    labels: ['On Time', 'Delayed'],
    datasets: [
      {
        data: prediction?.prediction === 'Delayed' ? [0, 100] : [100, 0],
        backgroundColor: ['rgba(75, 192, 192, 0.6)', 'rgba(255, 99, 132, 0.6)'],
      },
    ],
  };

  // Line Chart Data
  const lineData = {
    labels: ['Day 1', 'Day 2', 'Day 3', 'Day 4', 'Day 5'],
    datasets: [
      {
        label: 'Delay Probability (%)',
        data: delayHistory.map((value) => value * 100),
        borderColor: 'rgba(255, 99, 132, 1)',
        backgroundColor: 'rgba(255, 99, 132, 0.2)',
      },
    ],
  };

  return (
    <div className="chart-grid">
      <div className="chart-container">
        <Bar data={barData} options={{ responsive: true, plugins: { legend: { display: false } } }} />
      </div>

      <div className="chart-container">
        <Pie data={pieData} options={{ responsive: true }} />
      </div>

      <div className="chart-container">
        <Line data={lineData} options={{ responsive: true }} />
      </div>
    </div>
  );
};

export default Charts;
