import React, { useEffect, useState } from 'react';
import { Bar, Pie, Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  PointElement,
  LineElement,
  Interaction,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  PointElement,
  LineElement,
);

const Charts = ({ prediction }) => {
  const [delayHistory, setDelayHistory] = useState([0.1, 0.3, 0.2, 0.5, 0.7]); // Simulated trend data

  const handleBarClick = (event, elements) => {
    if (elements.length > 0) {
      const datasetIndex = elements[0].datasetIndex;
      const index = elements[0].index;
      alert(`Bar clicked: Dataset ${datasetIndex}, Index ${index}`);
    }
  };

  // Interactive Bar Chart Data
  const barData = {
    labels: ['Probability of Delay'],
    datasets: [
      {
        label: 'Probability (%)',
        data: [prediction ? prediction.probability_of_delay * 100 : 0],
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
        hoverBackgroundColor: 'rgba(75, 192, 192, 1)',
      },
    ],
  };

  const barOptions = {
    responsive: true,
    plugins: {
      legend: { display: false },
      tooltip: {
        callbacks: {
          label: (tooltipItem) => `${tooltipItem.raw}% Probability`,
        },
      },
    },
    onClick: handleBarClick,
  };

  // Interactive Pie Chart Data
  const pieData = {
    labels: ['On Time', 'Delayed'],
    datasets: [
      {
        data: prediction?.prediction === 'Delayed' ? [0, 100] : [100, 0],
        backgroundColor: ['rgba(75, 192, 192, 0.6)', 'rgba(255, 99, 132, 0.6)'],
        hoverOffset: 10, // Expand on hover
      },
    ],
  };

  const pieOptions = {
    responsive: true,
    plugins: {
      tooltip: {
        callbacks: {
          label: (tooltipItem) => `${tooltipItem.label}: ${tooltipItem.raw}%`,
        },
      },
    },
  };

  // Interactive Line Chart Data (Historical Trends)
  const lineData = {
    labels: ['Day 1', 'Day 2', 'Day 3', 'Day 4', 'Day 5'],
    datasets: [
      {
        label: 'Delay Probability (%)',
        data: delayHistory.map((value) => value * 100),
        borderColor: 'rgba(255, 99, 132, 1)',
        backgroundColor: 'rgba(255, 99, 132, 0.2)',
        pointRadius: 5,
        pointHoverRadius: 10, // Increase point size on hover
      },
    ],
  };

  const lineOptions = {
    responsive: true,
    plugins: {
      legend: { display: true },
      tooltip: {
        callbacks: {
          label: (tooltipItem) => `${tooltipItem.raw}% Probability`,
        },
      },
    },
    animations: {
      tension: {
        duration: 1000,
        easing: 'easeInOutBounce',
        from: 0.5,
        to: 0,
        loop: true,
      },
    },
  };

  return (
    <div className="chart-grid">
      <div className="chart-container">
        <Bar data={barData} options={barOptions} />
      </div>

      <div className="chart-container">
        <Pie data={pieData} options={pieOptions} />
      </div>

      <div className="chart-container">
        <Line data={lineData} options={lineOptions} />
      </div>
    </div>
  );
};

export default Charts;
