import React, { useState } from 'react';
import axios from 'axios';

const Form = ({ onPrediction }) => {
  const [formData, setFormData] = useState({
    depTime: '',
    crsArrTime: '',
    TaxiIn: 0,
    TaxiOut: 0,
    CarrierDelay: 0,
    WeatherDelay: 0,
    NASDelay: 0,
    LateAircraftDelay: 0,
  });

  const [error, setError] = useState('');

  const convertTimeToMinutes = (time) => {
    const [hours, minutes] = time.split(':').map(Number);
    return hours * 60 + minutes;
  };

  const validateInput = () => {
    if (!formData.depTime || !formData.crsArrTime) {
      setError('Departure and Arrival times are required.');
      return false;
    }
    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateInput()) return;

    try {
      const transformedData = {
        DepTimeMinutes: convertTimeToMinutes(formData.depTime),
        CRSArrTimeMinutes: convertTimeToMinutes(formData.crsArrTime),
        TaxiIn: parseFloat(formData.TaxiIn),
        TaxiOut: parseFloat(formData.TaxiOut),
        CarrierDelay: parseFloat(formData.CarrierDelay),
        WeatherDelay: parseFloat(formData.WeatherDelay),
        NASDelay: parseFloat(formData.NASDelay),
        LateAircraftDelay: parseFloat(formData.LateAircraftDelay),
      };

      const response = await axios.post('http://localhost:8000/predict', transformedData);
      onPrediction(response.data);
    } catch (err) {
      setError('Error processing prediction. Please try again.');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="max-w-lg mx-auto p-6 space-y-4">
      <h2 className="text-2xl font-semibold">Flight Delay Prediction</h2>
      {error && <p className="text-red-500">{error}</p>}

      <input type="time" name="depTime" onChange={(e) => setFormData({ ...formData, depTime: e.target.value })} required />
      <input type="time" name="crsArrTime" onChange={(e) => setFormData({ ...formData, crsArrTime: e.target.value })} required />

      <input type="number" name="TaxiIn" min="0" placeholder="Taxi In" onChange={(e) => setFormData({ ...formData, TaxiIn: e.target.value })} />
      <input type="number" name="TaxiOut" min="0" placeholder="Taxi Out" onChange={(e) => setFormData({ ...formData, TaxiOut: e.target.value })} />
      <input type="number" name="CarrierDelay" min="0" placeholder="Carrier Delay" onChange={(e) => setFormData({ ...formData, CarrierDelay: e.target.value })} />
      <input type="number" name="WeatherDelay" min="0" placeholder="Weather Delay" onChange={(e) => setFormData({ ...formData, WeatherDelay: e.target.value })} />
      <input type="number" name="NASDelay" min="0" placeholder="NAS Delay" onChange={(e) => setFormData({ ...formData, NASDelay: e.target.value })} />
      <input type="number" name="LateAircraftDelay" min="0" placeholder="Late Aircraft Delay" onChange={(e) => setFormData({ ...formData, LateAircraftDelay: e.target.value })} />

      <button type="submit" className="bg-blue-500 text-white p-2 rounded">Predict</button>
    </form>
  );
};

export default Form;
